import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';


const Weather = () =>{
    const [search,setSearch]= useState('noida');

    const [model ,setModel] = useState(null);

    const InputEvent = (event)=>{
        setSearch(event.input.value);
    }

    useEffect(()=>{
        const fetchApi = async() =>{
            const url = `http://api.openweathermap.org/data/2.5/weather?q=${search}&appid=6bcb87e868aec276a7232dea5006ce49`
            const response = await fetch(url);
            const resJson = await response.json();
            setModel(resJson);
        }
        fetchApi(); 
    })

    
    
    return(      

        <div className="col-md-4 col-10 mx-auto ">
            <div className="card" >                    
                    <div className="card-body">
                        <h5 className="card-title">{search}</h5>
                        <div className="input-group-sm">
                            <input type="search"
                                name="search"
                                value={search}
                                 onChange={InputEvent}></input>
                        </div>

                        <div className="text-info">
                            <p> location {model.city} and  country {model.country} and min temp {model.minTemp} and max temp {model.maxTemp}</p>
                        </div>
                       
                    </div>
            </div>                            
        </div>
       
    );
}

export default Weather;
