import React, { useEffect, useState } from 'react';



const Weather = () =>{
    const [search,setSearch]= useState('noida');

    const [model ,setModel] = useState(null);

   

    useEffect(()=>{
        const fetchApi = async() =>{
            const url = `http://api.openweathermap.org/data/2.5/weather?q=${search}&appid=6bcb87e868aec276a7232dea5006ce49`
            const response = await fetch(url);
            const resJson = await response.json();
            setModel(resJson);
        }
        fetchApi(); 
    },[search]);

    
    
    return(      

        <div className="col-md-4 col-10 mx-auto ">
            <div className="card" >                    
                    <div className="card-body">
                        <h5 className="card-title">{search}</h5>
                        <div className="input-group-sm">
                            <input type="search"
                                name="search"
                                value={search}
                                  onChange={(event) => {setSearch(event.target.value)}}></input>
                        </div>
                        {
                             !model ? (
                                 <p>No  data  found</p>
                             ) :(
                                    <div>
                                        <div className="text-info">
                                             <p> location {model.name} and  
                                             country {model.sys.country} and min temp {model.main.temp_min} 
                                             and max temp ${model.main.temp_max}</p>
                                        </div>

                                    </div>

                             )
                        }                       
                       
                    </div>
            </div>                            
        </div>
       
    );
}

export default Weather;
